        setComments(res.data);
    };

    useEffect(() => {
        fetchData();
    }, []);

    const renderedComments = comments.map(comment => {
        return <li key={comment.id}>{comment.content}</li>
    })

  return (
    <ul>
        {renderedComments}
    </ul>
  )
}

export default CommentList
